package com.example.vaadin;

import com.example.vaadin.model.Wrestler;
import com.example.vaadin.model.WrestlingOrganisationType;
import org.springframework.stereotype.Repository;

import java.util.ArrayList;
import java.util.List;

@Repository
public class WrestlerDeck {
    private List<Wrestler> wrestlerList;


    public WrestlerDeck() {
        this.wrestlerList = new ArrayList<>();
        Wrestler wrestler1 = new Wrestler("Ric Flair", WrestlingOrganisationType.WWE, "https://www.wwe.com/f/styles/wwe_large/public/rd-talent/Bio/Ric_Flair_bio.jpg");
        Wrestler wrestler2 = new Wrestler("Undertaker", WrestlingOrganisationType.WCW, "data:image/jpeg;base64,");
        Wrestler wrestler3 = new Wrestler("Hulk Hogan", WrestlingOrganisationType.AEW, "https://cdn3.whatculture.com/images/2018/09/74b15438c525028b-600x400.jpg");
        wrestlerList.add(wrestler1);
        wrestlerList.add(wrestler2);
        wrestlerList.add(wrestler3);
        
    }

    public List<Wrestler> getWrestlerList() {
        return wrestlerList;
    }

    public void setWrestlerList(List<Wrestler> wrestlerList) {
        this.wrestlerList = wrestlerList;
    }
}
